CREATE TABLE `vx_admin`
(
	`Username` varchar(50) NOT NULL,
	`Password` varchar(50) NOT NULL,
	PRIMARY KEY (`Username`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 ;


CREATE TABLE `vx_page`
(
  `PageID` int(11) NOT NULL AUTO_INCREMENT,
  `ProductID` int(11) NOT NULL,
  `ImageFile` varchar(50) NOT NULL,
  `PageIndex` int(11) DEFAULT NULL,
  PRIMARY KEY (`PageID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE `vx_product` (
  `ProductID` varchar(10) NOT NULL,
  `ProductCategoryID` varchar(10) NOT NULL,
  `ProductName` varchar(100) NOT NULL,
  `Author` varchar(255) DEFAULT NULL,
  `Year` varchar(20) DEFAULT NULL,
  `Size` varchar(50) DEFAULT NULL,
  `CoverType` varchar(50) NOT NULL,
  `Pages` int(11) DEFAULT '0',
  `Price` float NOT NULL,
  `Discount` float DEFAULT '0',
  `ISBN` varchar(20) DEFAULT NULL,
  `Shelf` int(11) NOT NULL,
  `Description` text,
  PRIMARY KEY (`ProductID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;


CREATE TABLE `vx_category`
(
	`ProductCategoryID` int(11) NOT NULL AUTO_INCREMENT,
	`ProductCategoryName` varchar(50) NOT NULL,
	PRIMARY KEY (`ProductCategoryID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;


CREATE TABLE `vx_order`
(
	`OrderID` varchar(50) NOT NULL,
	`CustomerName` varchar(255) NOT NULL,
	`CustomerAddress` TEXT NOT NULL,
	`CustomerEmail` varchar(255) NOT NULL,
	`UpdateDate` date NOT NULL,
	`OrderStatus` int(5) DEFAULT '0',
	`Comment` TEXT DEFAULT NULL,
	PRIMARY KEY (`OrderID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 ;

CREATE TABLE `vx_orderproduct`
(
	`OrderProductID` int(11) NOT NULL AUTO_INCREMENT,
	`OrderID` varchar(50) NOT NULL,
	`ProductID` varchar(10) NOT NULL,
	`Quantity` int(5) NOT NULL,
	`Price` float NOT NULL,
	PRIMARY KEY (`OrderProductID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;