INSERT INTO `vx_product` (`ProductID`, `ProductCategoryID`, `ProductName`, `Author`, `Year`, `Size`, `CoverType`, `Pages`, `Price`, `Discount`, `ISBN`, `Shelf`, `Description`) VALUES
('A001', '1', 'อรรถศาสตร์เบื้องต้น (INTRODUCTION TO SEMANTICS)', 'สุริยา รัตนกุล', '2 / 2555', '145 x 210 mm.', 'ปกอ่อน', 366, 260, 26, '9786162790492', 1, NULL),
('A002', '1', 'การเขียนเพื่อการสื่อสาร', 'วรวรรธน์ ศรียาภัย', '1 / 2555', '190 x 260 mm.', 'ปกอ่อน', 306, 210, 32, '9789740330318', 1, 'การเขียนเป็นทักษะการสื่อสารขั้นสูง จำเป็นสำหรับการศึกษาเรียนรู้ และการทำงานทุกสาขาอาชีพ ในด้านการศึกษา สิ่งที่ขาดมิได้คือ การเขียนลักษณะต่างๆ ทั้งย่อความ สรุปความจากการฟังอาจารย์สอน การอ่าน การดู จากแหล่งเรียนรู้ต่างๆ แล้วนำมาเขียนเป็นรายงาน ภาคนิพนธ์ หรือวิทยานิพนธ์ แม้แต่การสอบวัดผล ข้อสอบวัดความรู้ที่ดีที่สุดคือข้อสอบเขียนตอบ ยิ่งศึกษาระดับสูงขึ้นเท่าใด การเขียนก็มีบทบาทมากขึ้นเท่านั้น ในด้านการทำงาน จำเป็นอย่างยิ่งที่ต้องเขียนสื่อสารกันทั้งภายในและภายนอกองค์การ เพื่อให้งานดำเนินไปได้อย่างมีประสิทธิภาพ เช่น การเขียนหนังสือและจดหมายโต้ตอบ การเขียนโครงการ การเขียนบางอย่างก็สามารถใช้เป็นอาชีพได้ เช่น การเขียนบทความ การเขียนจึงเป็นทักษะสามัญที่มนุษย์ผู้ปรารถนาความเจริญก้าวควรฝึกฝนให้เกิดความชำนาญ สาระเนื้อหาสำคัญในหนังสือ ได้แก่ การสื่อสารและหลักภาษาไทยสำหรับการเขียนเพื่อการสื่อสาร การเขียนย่อ ย่อความ สรุปความ และขยายความ การเขียนเรียงความและบทความ การเขียนหนังสือราชการ การเขียนจดหมายโต้ตอบทางธุรกิจ การเขียนรายงานการประชุม การเขียนบันทึกและรายงาน แม้วิทยาศาสตร์และเทคโนโลยีสารสนเทศจะก้าวไปเพียงใดก็ตาม แต่การใช้ภาษาไทยเขียนเพื่อการสื่อสาร ก็ยังมีความสำคัญและจำเป็น สามารถประยุกต์ใช้ได้ควบคู่กันกับยุคสมัยแห่งการเปลี่ยนแปลง'),
('A003', '1', 'กลไกภาษาศาสตร์ (INTRODUCING LINGUISTICS)', 'R.L. TRASK และคณะ', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 176, 200, 30, '9786163070005', 1, 'เหตุที่การศึกษาด้านภาษาศาสตร์เป็นมากกว่าการอ่านออกเขียนได้ในแต่ละภาษา นั่นก็เพราะหนังสือเล่มนี้ถือเอาว่า “ภาษา” เป็นกระบวนการศึกษาค้นคว้าซึ่งเชื่อมโยงเอาองค์ความรู้ที่หลากหลายทั้งประวัติศาสตร์ จิตวิทยา มานุษยวิทยา และวรรณกรรมวิจารณ์ มาใช้อธิบายเพื่อทำความเข้าใจถึงกฎเกณฑ์ ระบบระเบียบ ตลอดจนลำดับชั้นทางภาษาฯลฯ โดยกรอบในการเรียนรู้เหล่านี้คงไม่พบในโรงเรียนสอนภาษาทั่วไปอย่างแน่นอน ภาษามีความสำคัญอย่างไรคงต้องไม่นำมากล่าวตรงนี้ แต่การเข้าใจถึงต้นกำเนิดวิวัฒนาการ กระทั่งความตายของภาษา ล้วนเป็นประเด็นที่ถูกหยิบยกนำมาแจกแจงอย่างครอบคลุมใน Introducing กลไกภาษาศาสตร์ อีกทั้งยังรวบยอดเอาบรรดาแนวคิดสำคัญของนักภาษาศาสตร์ผู้ยิ่งใหญ่ เช่น แฟร์ดิน๊อง เดอ โซซูร์ กับทฤษฎีโครงสร้าง , โรมัน จาคอบสัน กับสัญวิทยา , นอม ชอมสกี้กับทฤษฎีไวยากรณ์ปริวรรต , สตีเวน พิงเคอร์ กับจิตวิทยาของภาษา ฯลฯ ถือเป็นทางลัดย่อเพื่อให้มิตรนักอ่านได้ร่วมสำรวจเส้นทางของภาษาศาสตร์อย่างสั้นกระชับและครบถ้วนเชิงเนื้อหาไปพร้อมกัน'),
('A004', '1', 'การเขียนเพื่อการสื่อสาร (WRITING FOR COMMUNICATION)', 'ภาคภูมิ หรรนภา', '1 / 2554', '170 x 235 mm.', 'ปกอ่อน', 227, 220, 33, '9786167577029', 1, 'แนะนำการเขียนที่เป็นวิธีการสื่อสารที่มีประสิทธิภาพวิธีหนึ่ง ที่มนุษย์สามารถถ่ายทอดความรู้ ข้อเท็จจริง ความคิด จินตนาการ ทัศนคติ อารมณ์และความรู้สึกต่างๆ ได้เป็นอย่างดี และความสามารถของผู้เขียนเพื่อการสื่อสารก็มีความสำคัญเป็นอย่างมาก นอกจากนี้แล้วหนังสือเล่มนี้ ยังมีสาระเน้นหนักในการนำเสนอความรู้และหลักการเขียนเพื่อการสื่อสารเป็นทักษะภาษาที่สำคัญและยังมีบทบาทต่อชีวิตประจำวันของคนเราเป็นอย่างมาก  นอกจากนิสิต นักศึกษา ยังมีประโยชน์ต่อผู้ที่สนใจทั่วไปอีกด้วย เพราะช่วยเพิ่มเติมความรู้และทักษะในการเขียนแก่ผู้สนใจได้เป็นอย่างดี'),
('A005', '1', 'ลักษณะการใช้ศัพท์บาลีสันสกฤตในภาษาไทย', 'ชะเอม แก้วคล้าย', '1 / 2555', '185 x 260 mm.', 'ปกอ่อน', 134, 150, 15, '9786169042969', 1, NULL),
('A006', '1', 'LANGUAGE RESEARCH FOR TEACHERS :SEVEN-MODEL STUDIES', 'SUTTIRAT RUJIKIETGUMJORN และคณะ', '1 / 2553', '190 x 265 mm.', 'ปกอ่อน', 333, 340, 34, '9786162230257', 1, NULL),
('A007', '1', 'ภาษาศาสตร์คลังข้อมูล :หลักการและการใช้', 'วิโรจน์ อรุณมานะกุล', '2 / 2553', '145 x 210 mm.', 'ปกอ่อน', 387, 245, 37, '9786165511735', 1, 'หนังสือภาษาศาสตร์คลังข้อมูลตีพิมพ์ครั้งแรกในปี พ.ศ. 2545 การพิมพ์ปรับปรุงครั้งนี้ได้เพิ่มจำนวนจากเดิม 250 เป็นกว่า 380  ด้วยเหตุนี้จึงเปลี่ยนชื่อเรื่องใหม่เป็น ภาษาศาสตร์คลังข้อมูล : หลักการและการใช้ เพราะตั้งใจจะนำเสนอทั้งสองแนวทางคือเรื่องของหลักการและเรื่องวิธีการใช้ แนวทางแรกเป็นเรื่องหลักการความคิดต่างๆ ที่เกี่ยวข้องกับคลังข้อมูลภาษา และการใช้ประโยชน์จากคลังข้อมูลซึ่งเป็นเนื้อหาหลักของฉบับพิมพ์ครั้งแรก โดยบทที่กล่าวถึงเรื่องประโยชน์ของการใช้คลังข้อมูลภาษาเดิมที่เป็นเรื่องการเรียนการสอนภาษา การวิจัยภาษา การแปลและการประมวลผลภาษา ได้ถูกแยกออกมาเป็นแต่ละบทในฉบับนี้ให้จัดเจนขึ้น และได้เพิ่มประโยชน์ในการทำพจนานุกรม ตลอดจนกล่าวถึงคลังข้อมูลภาษา ผู้เรียนและการใช้ประโยชน์จากคลังข้อมูลภาษาผู้เรียนเพิ่มขึ้นมาอีกบทหนึ่ง'),
('A008', '1', 'ความรู้ความเข้าใจเรื่องภาษาเพื่อการแปล :จากทฤษฎีสู่การปฏิบัติ', 'รัชนีโรจน์ กุลธำรง', '1 / 2552', '190 x 260 mm.', 'ปกอ่อน', 317, 230, 34, '9789740323501', 1, 'จุดประสงค์สำคัญประการหนึ่งของการเขียนหนังสือเล่มนี้ ก็เพื่อเผยแพร่ความรู้ทางทฤษฎีที่ใช้ได้กับการแปล และเสนอแนวทางในการฝึกปฏิบัติโดยใช้ทฤษฎีเป็นหลัก ผู้เขียนต้องการเผยแพร่การใช้ความรู้ด้านทฤษฎีเป็นแนวทางให้เกิดความรู้ความเข้าใจเรื่องภาษา เพื่อให้สามารถนำไปใช้วิเคราะห์ตัวบทอย่างมีระบบและสามารถนำไปสังเคราะห์ใช้ในการตัดสินใจเมื่อต้องแก้ปัญหาในการแปล ตลอดจนเพื่อให้มีมุมมองที่หลากหลายในการแก้ปัญหา อีกทั้งเพื่อให้สามารถอธิบายได้อย่างมีหลักเกณฑ์ว่า ทำไมจึงตัดสินใจเลือกคำแปลเช่นนั้นแทนที่จะอธิบายเพียงว่า “ฟังดูดี ฟังดูเพราะดี” อย่างที่เรามักได้ยินกันอยู่ รัชนีโรจน์ กุลธำรง'),
('A009', '1', 'แปลได้ แปลดี :ทักษะการแปลสำหรับผู้เรียนในระดับมหาวิทยาลัย', 'สุพรรณี ปิ่นมณี', '3 / 2555', '145 x 210 mm.', 'ปกอ่อน', 228, 110, 16, '9789740324409', 1, 'แปลได้ แปลดี มีเนื้อหาที่ทันสมัย เหมาะเป็นคู่มือสำหรับอาจารย์ นิสิต นักศึกษาทุกสถาบันที่มีการเรียนการสอนวิชาภาษาอังกฤษและการแปล เพราะจะช่วยให้สามารถแปลได้และแปลได้ดีด้วย (be able to translate and to translate well) ในขณะเดียวกัน “แปลได้แปลดี” (be joyful in translation) กล่าวคือ มีความสุขสนุกสนานเพลิดเพลินกับการแปลจนแปลได้ดีทั้งในเชิงคุณภาพและปริมาณ นอกจากนั้นยังมุ่งเน้นที่จะสร้างพื้นฐานทางการใช้ภาษาของผู้เรียนให้มั่นคงแข็งแรงอันจะนำไปสู่การพัฒนาทักษะการแปลทั้งจากอังกฤษเป็นไทย และจากไทยเป็นอังกฤษในระดับกลาง เพื่อต่อยอดไปสู่การแปลในระดับสูงต่อไป แปลได้ แปลดี ให้พื้นฐานในการใช้ภาษากับการแปลที่เหมาะสม สอดคล้องกับความจำเป็นและความประสงค์ของทุกท่านที่สนใจการแปล สามารถนำไปใช้งานได้จริง'),
('A010', '1', 'MI FU ON INK-STONES (HC)', 'VAN GULIK, R.H.', '1 / 2006', '175 x 250 mm.', 'ปกแข็ง', 70, 1, 135, '9745240834', 1, NULL),
('A011', '1', 'ENGLISH HISTORICAL METRICS (ED.1/1996)', 'MCCULLY, C.B.', '1 / 2006', '155 x 225 mm.', 'ปกอ่อน', 257, 1, 328, '0521026288', 1, NULL),
('A012', '1', 'WORDS ON WORDS: QUOTATIONS ABOUT LANGUAGE AND LANGUAGES (HC)', 'CRYSTAL, D.', '1 / 2000', '150 x 240 mm.', 'ปกแข็ง', 580, 1, 354, '0226122018', 1, NULL),
('A013', '1', 'FROM ANCIENT THAI TO MODERN DIALECTS', 'J. MARVIN BROWN', '1 / 2007', '150 x 225 mm.', 'ปกอ่อน', 264, 850, 85, '9748495078', 1, NULL),
('A014', '1', 'พูดเกาหลีในชีวิตประจำวัน 2 + 2 Audio CD', 'กองบรรณาธิการ เอ็มไอเอสซอฟท์เทค', '7 / 2549', '130 x 185 mm.', 'ปกอ่อน', 130, 199, 0, NULL, 1, 'ในปัจจุบันภาษาเกาหลีเป็นอีกหนึ่งภาษาที่ได้รับความนิยมและเป็นที่สนใจอย่างมาก สื่อการสอนชุด "พูดเกาหลีในชีวิตประจำวัน" จึงเหมาะอย่างยิ่งสำหรับผู้ที่สนในเรียนรู้ภาษาเกาหลี เพือการนำไปประยุกต์ใช้ในชีวิตประจำวัน โดยเนื้อหาประกอบด้วยบท สนทนาในสถานการณ์ต่างๆ ซึ่งสามารถเรียนรู้ได้ง่ายด้วยตนเอง พร้อมฝึกทักษะการฟัง และการออกเสียงจากเจ้าของภาษา'),
('A015', '1', 'พูดเกาหลีในชีวิตประจำวัน 1 + 2 Audio CD', 'LEE PU YOUNG', '6 / 2549', '130 x 185 mm.', ' ปกอ่อน', 111, 199, 0, '9749428706', 1, 'ในปัจจุบันภาษาเกาหลีเป็นอีกหนึ่งภาษาที่ได้รับความนิยมและเป็นที่สนใจอย่างมาก สื่อการสอนชุด "พูดเกาหลีในชีวิตประจำวัน" จึงเหมาะอย่างยิ่งสำหรับผู้ที่สนในเรียนรู้ภาษาเกาหลี เพือการนำไปประยุกต์ใช้ในชีวิตประจำวัน โดยเนื้อหาประกอบด้วยบท สนทนาในสถานการณ์ต่างๆ ซึ่งสามารถเรียนรู้ได้ง่ายด้วยตนเอง พร้อมฝึกทักษะการฟัง และการออกเสียงจากเจ้าของภาษา'),
('A016', '1', 'แบบฝึกเขียนอ่านภาษาเกาหลีขั้นพื้นฐาน', 'บุญมา พิพิธธนา', '1 / 2548', '185  x 260 mm.', 'ปกอ่อน  / 199', NULL, 185, 0, '9749302451', 1, 'หนังสือฝึกภาษาเกาหลีที่คุณสามารถเรียนรู้ได้ด้วยตนเองแม้ไม่แคยมีพื้นฐานภาษาเกาหลีมาก่อน เนื่องจากมีคำแปล คำอ่านภาษา ไทย เทียบเสียงพยัญชนะ สระภาษาเกาหลีกับเสียงที่มีในภาษาไทยโดยเลือกพยัญชนะ สระที่มีเสียงใกล้เคียงกันมากที่สุด \n\n     เนื้อหาประกอบด้วย 1. วิธีเขียนและอ่านพยัญชนะ สระ และตัวสะกด 2. แบบฝึกอ่านตัวอักษร คำศัพท์พร้อมรูป โดยมีคำอ่านภาษาเกาหลี-ไทย 3. คำอธิบายภาพประกอบ และ 4.แบบฝึกหัด'),
('A017', '1', 'ตัวช่วยไวยากรณ์อังกฤษ', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 32, 69, 0, '9789740616818', 1, 'ตัวช่วยที่จะทำให้คุณเรียนรู้หลักไวยากรณ์อังกฤษ ได้อย่างเข้าใจได้มากขึ้น ครอบคลุมทุกเรื่องที่คุณควรจะต้องเรียนรู้เกี่ยวกับหลักไวยากรณ์ภาษาอังกฤษ'),
('A018', '1', 'เริ่มเรียนภาษาจีน (4)', 'ก่อศักดิ์ ธรรมเจริญกิจ', '1 / 2552', '188x260x4 mm.', 'ปกอ่อน', 32, 28, 0, '9789990003468', 1, 'เริ่มเรียนภาษาจีน ทั้งหมดมี 6 เล่ม เป็นตำราที่ออกแบบสำหรับเด็กนักเรียนไทยโดยเฉพาะ เหมาะสำหรับนักเรียนอนุบาลหรือนักเรียนระดับประถมศึกษาที่เริ่มเรียนภาษาจีนโดยทั่วไป นักเรียนที่เรียนจบ เริ่มเรียนภาษาจีน เล่ม 4 สามารถเข้าใจหลักการเขียนอักษรที่ใช้บ่อยอย่างถูกต้องอีก 30 คำ เรียนรู้คำแบบ 3 พยางค์จากภาพประกอบอีก 30 คำ คำผสมและวลีอีก 130 คำ'),
('A019', '1', 'คัดเกาหลี', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 65, 0, '9789749764237', 1, 'ฝึกคัดตัวอักษร เรียนคำศัพท์ พร้อมสนุกกับเกมส์ที่ช่วยให้จดจำ ตัวอักษรและคำศัพท์ภาษาเกาหลีได้แม่นยำ'),
('A020', '1', 'เรียนลัดไวยากรณ์จากการสนทนา', 'สรรเสริญ สุวรรณประเทศ', '1 / 2552', '290x420x22 มม.', 'ปกอ่อน', 200, 135, 0, '9789746138437', 1, 'ในการสื่อสารด้วยภาษาอังกฤษให้บรรลุผล นอกจากจะต้องรู้ศัพท์และ\nสำนวนต่างๆแล้วยังจำเป็นต้องรู้หลักไวยากรณ์อีกด้วย ทั้งนี้\nเพราะไวยากรณ์เป็นโครงสร้างของภาษา หากรู้แต่ศัพท์สำนวน\nไม่รู้ไวยากรณ์ก็ย่อมไม่สามารถผูกประโยคได้\nGrammar Through Conversation เรียนลัดไวยากรณ์จากการสนทนา\nจะช่วยปูพื้นฐานไวยากรณ์อังกฤษ แก่ผู้เรียนด้วยวิธีที่ง่ายดาย\nเป็นธรรมชาติ และไม่น่าเบื่อ ด้วยการศึกษาจากการสนทนา\nในชีวิตประจำวันอันเป็นวิธีที่ประยุกต์มาจากการเรียนรู้ภาษา\nตามธรรมชาติ หนังสือเล่มนี้มิได้มุ่งปูพื้นฐาน\nไวยากรณ์อังกฤษเหมือนหนังสือไวยากรณ์ทั่วไป\nหากแต่นำเสนอหลักไวยากรณ์ที่เป็นพื้นฐานสำคัญสำหรับ\nการสนทนาภาษาอังกฤษผ่านทางบทสนทนาที่ถูกออกแบบมาเป็นพิเศษ\nซึ่งจะทำให้ผู้เรียนเข้าใจหลักไวยากรณ์ไปพร้อมกับเพิ่มพูนทักษะการสนทนา\nจึงเหมาะสำหรับผู้ที่ต้องการปูพื้นฐานหรือทบทวนหลักไวยากรณ์และ\nพัฒนาขีดความสามารถด้านการสนทนาในเวลาอันรวดเร็ว'),
('A021', '1', 'เรียนภาษาเกาหลีเบื้องต้น', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 240, 0, '9789749764145', 1, 'สำหรับผู้ที่ต้องการเรียนรู้ภาษาเกาหลีเบื้องต้น พบกับการเรียนรู้รูปแบบใหม่ VCD ภาพพร้อมตัวอย่างช่วยให้เข้าใจบทเรียนง่ายขึ้น เนื้อหาอธิบายเป็นภาษาไทย เสียงภาษาเกาหลีจาก Native Speakers สอนตั้งแต่ระดับพื้นฐาน ฝึกทำแบบฝึกหัดทบทวนพร้อมเฉย\nภาษาเกาหลีเริ่มเข้ามามีบทบาทต่อสังคมไทย ไม่ว่าจะเป็นเพลง ละคร อาหาร แฟชั่น หรือแม้แต่การเรียนภาษาเกาหลี ทำให้คนไทยสนใจเรียนภาษาเกาหลีกันมากขึ้น สื่อการสอนชุด "เรียนภาษาเกาหลีเบื้องต้น" มีเนื้อหาตั้งแต่ตัวอักษรในภาษาเกาหลี ไวยากรณ์ขั้นพื้นฐาน คำศัพท์ และบทสนทนา ซึ่งในแต่ละบทเรียนจะมีแบบฝึกหัดให้ทบทวน พร้อมฝึกฟังประโยคสนทนาที่ใช้ในชีวิตประจำวันจากเจ้าของภาษา ซึ่งทำให้สามารถเรียนรู้ได้ง่ายด้วยตัวเอง'),
('A022', '1', 'สนทนาภาษาเกาหลี 1,000 ประโยค', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 240, 0, '9789749469927', 1, '1,000 ประโยคสนทนาภาษาเกาหลีพร้อมใช้ ช่วยให้สามารถพูด ภาษาเกาหลีได้ง่ายขึ้น Conversation by Native Speakers เพื่อฝึกทักษะ การออกเสียงที่ถูกต้อง \nสื่อการสอนชุด "สนทนาภาษาเกาหลี 1,000 ประโยค" เหมาะสำหรับผู้ที่สนใจเรียนภาษาเกาหลี นักเรียน นักศึกษา และบุคคลทั่วไปที่ต้องการจะฝึกพูดภาษาเกาหลี เนื้อหาเน้นการสนทนาในสถานการณ์ต่างๆ ที่มักเจอในชีวิตประจำวัน เป็นประโยคสนทนาแบบสั้นๆ รวบรวมไว้มากถึง 1,000 ประโยค ผู้เรียนจะได้ฝึกฟัง และพูดตามเจ้าของภาษา ซึ่งจะช่วยให้พูดภาษาเกาหลีได้ง่ายขึ้น'),
('A023', '1', 'คัดจีน เล่ม 1', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 49, 0, '9789749449202', 1, 'สมุดคัดจีน สำหรับผู้ที่ต้องการฝึกเขียนอักษรจีน ระหว่างคุณคัดอักษรจีน คุณได้จะเรียนรู้ทั้งลำดับขีด, กลุ่มคำที่เกี่ยวข้อง และตัวอย่างประโยค\nการรู้จักตัวอักษรจีนนับว่ามีความสำคัญเป็นอันดับแรกๆ ของการเรียนภาษาจีน ก่อนที่จะรู้ถึงความหมายของคำก็ต้องรู้ถึงลำดับขีดเสียก่อน หากเข้าใจถึงวิธีการเขียนตามลำดับขีดอย่างถูกต้อง ไม่เพียงแต่จะทำให้เขียนได้อย่างสวยงาม แต่ยังสามารถนำไปหาความหมายที่แท้จริงของคำนั้นโดยไม่ผิดเพี้ยน\nเหมาะสำหรับผู้ที่ต้องการฝึกเขียนอักษรจีน ช่วยทำให้มีทักษะในการเขียนกับแบบฝึกคัดอักษรจีน ได้เป็นอย่างดี ภายใน 1 คำ'),
('A024', '1', 'คัดจีน เล่ม 2', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 49, 0, '9789744510082', 1, 'สมุดคัดจีน สำหรับผู้ที่ต้องการฝึกเขียนอักษรจีน ระหว่างคุณคัดอักษรจีน คุณได้จะเรียนรู้ทั้งลำดับขีด, กลุ่มคำที่เกี่ยวข้อง และตัวอย่างประโยค\nการรู้จักตัวอักษรจีนนับว่ามีความสำคัญเป็นอันดับแรกๆ ของการเรียนภาษาจีน ก่อนที่จะรู้ถึงความหมายของคำก็ต้องรู้ถึงลำดับขีดเสียก่อน หากเข้าใจถึงวิธีการเขียนตามลำดับขีดอย่างถูกต้อง ไม่เพียงแต่จะทำให้เขียนได้อย่างสวยงาม แต่ยังสามารถนำไปหาความหมายที่แท้จริงของคำนั้นโดยไม่ผิดเพี้ยน'),
('A025', '1', 'คัดจีน เล่ม 3', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 49, 0, '9786115270101', 1, 'สมุดแบบฝึกหัดคัดจีน สำหรับผู้ที่ต้องการฝึกเขียนอักษรจีน ระหว่างคุณคัดอักษรจีน คุณได้จะเรียนรู้ทั้งลำดับขีดที่ถูกต้อง, การอ่านออกเสียงจีนกลาง, และการอ่านออกเสียงแต้จิ๋ว กลุ่มคำของอักษรจีน และมีคำศัพท์ภาษาอังกฤษ \n\nการรู้จักตัวอักษรจีนนับว่ามีความสำคัญเป็นอันดับแรกๆ ของการเรียนภาษาจีน ก่อนที่จะรู้ถึงความหมายของคำก็ต้องรู้ถึงลำดับขีดเสียก่อน หากเข้าใจถึงวิธีการเขียนตามลำดับขีดอย่างถูกต้อง ไม่เพียงแต่จะทำให้เขียนได้อย่างสวยงาม แต่ยังสามารถนำไปหาความหมายที่แท้จริงของคำนั้นโดยไม่ผิดเพี้ยน'),
('A026', '1', 'ชุด พูด เขียน เรียน ภาษาจีน', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 499, 0, '9789744510037', 1, 'สุดยอดสื่อการเรียนรู้ภาษาจีนระดับเบื้องต้น ที่ประสบความสำเร็จได้ เหมาะสำหรับผู้ที่ไม่มีพื้นฐานภาษาจีนมาก่อน เนื้อหาประกอบด้วย หลักเกณฑ์การเขียนอักษรจีน โครงสร้างทางภาษา ไวยากรณ์ และการสนทนา'),
('A027', '1', 'พูดจีนธุรกิจ', 'กองบรรณาธิการเอ็มไอเอส\nกองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 240, 0, '9789749387443', 1, 'พูดจีนธุรกิจ (ฉบับสมบูรณ์) เป็นคู่มือการเรียนรู้ ภาษาจีนในวงการธุรกิจ ด้วยตนเอง'),
('A028', '1', 'ศัพท์จีนกลาง สำหรับผู้เริ่มเรียน', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 80, 0, '9786115270323', 1, 'เรียนรู้คำศัพท์ภาษาจีนเบื้องต้น เพื่อฝึกใช้ร่วมกับทักษะ ฟัง-พูด-อ่าน-เขียน ก่อนเรียนในระดับสูง Chinese Vocabulary รวมคำศัพท์ที่ใช้ร่วมกับทักษะ ฟัง-พูด-อ่าน-เขียน, หลักการอ่านตามสัทอักษรพินอิน มีคำอ่านไทยและคำแปล, จัดหมวดหมู่ตามความสัมพันธ์ของคำ 9 กลุ่มใหญ่ 56 กลุ่มย่อย เหมาะสำหรับผู้เริ่มต้นเรียนภาษาจีน'),
('A029', '1', 'คัดญี่ปุ่น', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 74, 0, '9786165270038', 1, 'หนังสือคัดญี่ปุ่น สำหรับผู้ที่ต้องการฝึกเขียนอักษรญี่ปุ่น'),
('A030', '1', '1,000 Idioms ฝรั่งพูดคนไทยเข้าใจ', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 149, 0, '9789749764541', 1, 'A-Z Idioms สุดยอดสำนวนใช้บ่อย ทั้งแบบอังกฤษและอเมริกันกว่า 1,000 สำนวน พร้อมตัวอย่างบทสนทนากว่า 2,000 ประโยค ที่ฝรั่งนิยมใช้ เพื่อให้การสนทนาลื่นไหลและเข้าใจตรงกัน ที่จะทำให้ให้คุณพูดคล่อง ฟังเข้าใจได้เหมือนเจ้าของภาษา\n\nหลายครั้งหลายครา ที่คนไทยต้องเกิดอาการงงงวย เมื่อต้องสนทนากับฝรั่งเจ้าสำบัดสำนวน เพราะเจ้าของภาษามักจะพ่วงสำนวนและศัพท์สแลงต่างๆ มาด้วยเสมอ หากแปลแบบตรงตัว ก็จะทำให้ความหมายผิดเพี้ยนจนกลายเป็นการเข้าใจผิด และนำไปสู่ความล้มเหลวในการสื่อสารไปในที่สุด\n\n1,000 Idioms ฝรั่งพูดคนไทยเข้าใจ จะช่วยให้ผู้อ่านคนไทยสามารถ จูน คลื่นความคิดให้ตรงกับฝรั่ง เมื่อต้องสนทนากัน โดยผ่านสำนวนที่น่าสนใจ และใช้บ่อยทั้งแบบอังกฤษและอเมริกัน พร้อมตัวอย่างบทสนทนากว่า 2,000 ประโยค เพื่อให้ผู้เรียนได้เห็นบริบทการใช้ สามารถเข้าใจความหมาย และสื่อสารได้อย่างมีประสิทธิภาพ'),
('A031', '1', '3 Steps พูดอังกฤษให้สำเนียงเหมือนฝรั่ง', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 240, 0, '9789749764817', 1, 'สำเนียงฝรั่ง ทำได้ง่ายนิดเดียว! คู่มือฝึกออกเสียงให้เหมือนฝรั่ง ฉบับแรกที่ถูกถ่ายทอดมาเพื่อคนไทย โดย Teacher Ben ผู้มีประสบการณ์สอนในประเทศไทย และใช้ชีวิตในอเมริกา รวมกว่า 10 ปี เรียนทีละขั้นตอน Step by Step'),
('A032', '1', 'พูดอังกฤษธุรกิจ', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 240, 0, '9789742580209', 1, 'รวมบทสนทนา สำหรับติดต่อประสานงานที่ตรงกับ สถานการณ์จริง ทั้งภายใน - นอกองค์กร เนื้อหาครบถ้วนสมบูรณ์แบบ ออกเสียงสนทนาโดยชาวอังกฤษและอเมริกัน \n\nภาษาอังกฤษมีความสำคัญอย่างยิ่งต่อการทำงาน ไม่ว่าจะเป็นการติดต่อประสานงานภายในองค์กร หรือการติดต่อเจรจาธุรกิจ ซึ่งผู้ที่มีความเชี่ยวชาญทักษะการฟัง พูด อ่าน และเขียนภาษาอังกฤษได้คล่องแคล่วนั้น ย่อมได้เปรียบกว่าในหลายๆ ด้าน อีกทั้งยังช่วยเพิ่มความน่าเชื่อถือได้เป็นอย่างดี\n\nสื่อการสอนชุด พูดอังกฤษธุรกิจ จัดทำขึ้นเพื่อให้ผู้ที่สนใจได้ฝึกฝนภาษาอังกฤษที่ตรงกับการใช้งานจริง โดยมีเนื้อหา 15 บท ในแต่ละบทประกอบด้วย บทสนทนาที่จำลองมาจากสถานการณ์จริง มีคำอ่านออกเสียงเป็นภาษาไทย คำศัพท์ที่เกี่ยวข้อง ทบทวนไวยากรณ์ และนานาสาระน่ารู้เกี่ยวกับภาษาอังกฤษ เพื่อพัฒนาความสามารถด้านการสื่อสาร ที่จะช่วยเพิ่มความก้าวในที่การงาน และโอกาสทางธุรกิจให้มากยิ่งขึ้น'),
('A033', '1', 'พูดอังกฤษให้ฝรั่งเข้าใจ', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 99, 0, '9789749764015', 1, 'สื่อการเรียนการสอนชุด "พูดอังกฤษให้ฝรั่งเข้าใจ"\nคุณเคยไหมที่แอบมองคนไทยบางคนพูกคุยสนทนากับนักท่องเที่ยวต่างชาติอย่างสนุกสนาน หรือว่าเคยไหมที่อยากจะช่วยชาวต่างชาติที่มาถามทางตามท้องถนนแต่ไม่รู้จะพูดยังไงหรือบางทีมีฝรั่งอยากจะคุยด้วย แต่เราก็คุยกับเขาไม่รู้เรื่อง หลายคนคิดว่าปัญหาเหล่านี้เกิดจากตนเองเรียนภาษาอังกฤษมาไม่เพียงพอต่อการนำไปใช้ แต่ก็เกิดคำถามขึ้นอีกว่าเพราะเหตุใดพ่อค้าแม่ค้าบางคนที่อาจจะไม่ได้เรียนมามากมาย จึงกลับสามารถสื่อสารกับชาวต่างชาติได้อย่างรู้เรื่อง นั่นก็เพราะว่าเขามีการนำไปใช้งานจริงและที่สำคัญคือเขากล้าที่จะพูด สือ่การสอนชุดนี้คือสิ่งที่จะมาช่วยสร้างความมั่นใจในการพูดภาษาอังกฤษให้กับคุณ เพื่อเติมเต็มความสามารถของคุณให้ประสบผลสำเร็จในการสื่อสารกับฝรั่งได้อย่างราบรื่น'),
('A034', '1', 'ภาษาอังกฤษเพื่อการทำงาน', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 250, 0, '9789748280318', 1, 'สื่อการสอนชุด "ภาษาอังกฤษเพื่อการทำงาน"ได้รวบรวมเอาคำศัพท์ บทสนทนา รวมทั้งเกร็ดความรู้ที่จำเป็นต่างๆ ในสำนักงาน มาไว้ครบถ้วน เริ่มตั้งแต่การสมัครงานจนถึงการนำเสนอผลงาน เหมาะสำหรับ ผู้ที่ต้องทำงานร่วมกับ ชาวต่างชาติ ไม่ว่าจะเป็น เพื่อนร่วมงาน เจ้านาย หรือลูกค้า สื่อการสอนชุดนี้จะช่วย ให้คุณเชี่ยวชาญในการ ใช้ภาษาอังกฤษในที่ทำงาน ขจัดความประหม่า ในการพูดและการสื่อสาร ซึ่งนำไปสู่บรรยากาศการทำงานอันราบรื่น และสัมฤทธิ์ผล'),
('A035', '1', 'หนังสือภาษาอังกฤษเพื่อการเดินทาง', 'กองบรรณาธิการเอ็มไอเอส', '1 / 2552', NULL, 'ปกอ่อน', 0, 195, 0, '9789749394311', 1, 'สื่อการสอนชุดนี้ ได้รวบรวมเนื้อหา และบทสนทนาภาษาอังกฤษในสถานการณ์ต่างๆ มากกว่า 60 สถานการณ์ ที่นักเดินทาง หรือนักท่องเที่ยวจำเป็นต้องใช้ เช่น การทักทาย, การสอบถามข้อมูล, ร้านอาหาร, การจองตั๋วเครื่องบิน, ด่านตรวจคนเข่าเมือง ไปจนถึงการแลกเงิน เหมาะสำหรับนักเดิน'),
