CREATE TABLE `vx_page`
(
  `PageID` int(11) NOT NULL AUTO_INCREMENT,
  `ProductID` int(11) NOT NULL,
  `ImageFile` varchar(50) NOT NULL,
  `PageIndex` int(11) DEFAULT NULL,
  PRIMARY KEY (`PageID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

CREATE TABLE `vx_product`
(
	`ProductID` varchar(10) NOT NULL,
	`ProductCategoryID` varchar(10) NOT NULL,
	`ProductName` varchar(100) NOT NULL,
	`Author` varchar(255) DEFAULT NULL,
	`Year` varchar(20) DEFAULT NULL,
	`Size` varchar(20) DEFAULT NULL,
	`CoverType` varchar(50) NOT NULL,
	`Pages` int(11) DEFAULT '0',
	`Price` float NOT NULL,
	`Discount` float DEFAULT '0',
	`ISBN` int(11) DEFAULT NULL,
	`Shelf` int(11) NOT NULL,
	`Description` TEXT DEFAULT NULL,
	PRIMARY KEY (`ProductID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 ;

INSERT INTO `vx_product` VALUES
('A001', 1, 'อรรถศาสตร์เบื้องต้น (INTRODUCTION TO SEMANTICS)', 'สุริยา รัตนกุล', '2 / 2555', '145 x 210 mm.', 'ปกอ่อน', 366, 260.00, 26.00, 9786162790492, 1, NULL),
('A002', 1, 'การเขียนเพื่อการสื่อสาร', 'วรวรรธน์ ศรียาภัย', '1 / 2555', '190 x 260 mm.', 'ปกอ่อน', 306, 210.00, 32.00, 9789740330318, 1, 'การเขียนเป็นทักษะการสื่อสารขั้นสูง จำเป็นสำหรับการศึกษาเรียนรู้ และการทำงานทุกสาขาอาชีพ ในด้านการศึกษา สิ่งที่ขาดมิได้คือ การเขียนลักษณะต่างๆ ทั้งย่อความ สรุปความจากการฟังอาจารย์สอน การอ่าน การดู จากแหล่งเรียนรู้ต่างๆ แล้วนำมาเขียนเป็นรายงาน ภาคนิพนธ์ หรือวิทยานิพนธ์ แม้แต่การสอบวัดผล ข้อสอบวัดความรู้ที่ดีที่สุดคือข้อสอบเขียนตอบ ยิ่งศึกษาระดับสูงขึ้นเท่าใด การเขียนก็มีบทบาทมากขึ้นเท่านั้น ในด้านการทำงาน จำเป็นอย่างยิ่งที่ต้องเขียนสื่อสารกันทั้งภายในและภายนอกองค์การ เพื่อให้งานดำเนินไปได้อย่างมีประสิทธิภาพ เช่น การเขียนหนังสือและจดหมายโต้ตอบ การเขียนโครงการ การเขียนบางอย่างก็สามารถใช้เป็นอาชีพได้ เช่น การเขียนบทความ การเขียนจึงเป็นทักษะสามัญที่มนุษย์ผู้ปรารถนาความเจริญก้าวหน้าควรฝึกฝนให้เกิดความชำนาญ สาระเนื้อหาสำคัญในหนังสือ ได้แก่ การสื่อสารและหลักภาษาไทยสำหรับการเขียนเพื่อการสื่อสาร การเขียนย่อหน้า ย่อความ สรุปความ และขยายความ การเขียนเรียงความและบทความ การเขียนหนังสือราชการ การเขียนจดหมายโต้ตอบทางธุรกิจ การเขียนรายงานการประชุม การเขียนบันทึกและรายงาน แม้วิทยาศาสตร์และเทคโนโลยีสารสนเทศจะก้าวหน้าไปเพียงใดก็ตาม แต่การใช้ภาษาไทยเขียนเพื่อการสื่อสาร ก็ยังมีความสำคัญและจำเป็น สามารถประยุกต์ใช้ได้ควบคู่กันกับยุคสมัยแห่งการเปลี่ยนแปลง'),
('A003', 1, 'กลไกภาษาศาสตร์ (INTRODUCING LINGUISTICS)', 'R.L. TRASK และคณะ', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 176, 200.00, 30.00, 9786163070005, 1, 'เหตุที่การศึกษาด้านภาษาศาสตร์เป็นมากกว่าการอ่านออกเขียนได้ในแต่ละภาษา นั่นก็เพราะหนังสือเล่มนี้ถือเอาว่า “ภาษา” เป็นกระบวนการศึกษาค้นคว้าซึ่งเชื่อมโยงเอาองค์ความรู้ที่หลากหลายทั้งประวัติศาสตร์ จิตวิทยา มานุษยวิทยา และวรรณกรรมวิจารณ์ มาใช้อธิบายเพื่อทำความเข้าใจถึงกฎเกณฑ์ ระบบระเบียบ ตลอดจนลำดับชั้นทางภาษาฯลฯ โดยกรอบในการเรียนรู้เหล่านี้คงไม่พบในโรงเรียนสอนภาษาทั่วไปอย่างแน่นอน ภาษามีความสำคัญอย่างไรคงต้องไม่นำมากล่าวตรงนี้ แต่การเข้าใจถึงต้นกำเนิดวิวัฒนาการ กระทั่งความตายของภาษา ล้วนเป็นประเด็นที่ถูกหยิบยกนำมาแจกแจงอย่างครอบคลุมใน Introducing กลไกภาษาศาสตร์ อีกทั้งยังรวบยอดเอาบรรดาแนวคิดสำคัญของนักภาษาศาสตร์ผู้ยิ่งใหญ่ เช่น แฟร์ดิน๊อง เดอ โซซูร์ กับทฤษฎีโครงสร้าง , โรมัน จาคอบสัน กับสัญวิทยา , นอม ชอมสกี้กับทฤษฎีไวยากรณ์ปริวรรต , สตีเวน พิงเคอร์ กับจิตวิทยาของภาษา ฯลฯ ถือเป็นทางลัดย่อเพื่อให้มิตรนักอ่านได้ร่วมสำรวจเส้นทางของภาษาศาสตร์อย่างสั้นกระชับและครบถ้วนเชิงเนื้อหาไปพร้อมกัน'),
('A004', 1, 'การเขียนเพื่อการสื่อสาร (WRITING FOR COMMUNICATION)', 'ภาคภูมิ หรรนภา', '1 / 2554', '170 x 235 mm.', 'ปกอ่อน', 227, 220.00, 33.00, 9786167577029, 2, 'แนะนำการเขียนที่เป็นวิธีการสื่อสารที่มีประสิทธิภาพวิธีหนึ่ง ที่มนุษย์สามารถถ่ายทอดความรู้ ข้อเท็จจริง ความคิด จินตนาการ ทัศนคติ อารมณ์และความรู้สึกต่างๆ ได้เป็นอย่างดี และความสามารถของผู้เขียนเพื่อการสื่อสารก็มีความสำคัญเป็นอย่างมาก นอกจากนี้แล้วหนังสือเล่มนี้ ยังมีสาระเน้นหนักในการนำเสนอความรู้และหลักการเขียนเพื่อการสื่อสารเป็นทักษะภาษาที่สำคัญและยังมีบทบาทต่อชีวิตประจำวันของคนเราเป็นอย่างมาก  นอกจากนิสิต นักศึกษา ยังมีประโยชน์ต่อผู้ที่สนใจทั่วไปอีกด้วย เพราะช่วยเพิ่มเติมความรู้และทักษะในการเขียนแก่ผู้สนใจได้เป็นอย่างดี'),
('A005', 1, 'ลักษณะการใช้ศัพท์บาลีสันสกฤตในภาษาไทย', 'ชะเอม แก้วคล้าย', '1 / 2555', '185 x 260 mm.', 'ปกอ่อน', 134, 150.00, 15.00, 9786169042969, 2, NULL),
('A006', 1, 'LANGUAGE RESEARCH FOR TEACHERS :SEVEN-MODEL STUDIES', 'SUTTIRAT RUJIKIETGUMJORN และคณะ', '1 / 2553', '190 x 265 mm.', 'ปกอ่อน', 333, 340.00, 34.00, 9786162230257, 2, NULL),
('A007', 1, 'ภาษาศาสตร์คลังข้อมูล :หลักการและการใช้', 'วิโรจน์ อรุณมานะกุล', '2 / 2553', '145 x 210 mm.', 'ปกอ่อน', 387, 245.00, 37.00, 9786165511735, 2, 'หนังสือภาษาศาสตร์คลังข้อมูลตีพิมพ์ครั้งแรกในปี พ.ศ. 2545 การพิมพ์ปรับปรุงครั้งนี้ได้เพิ่มจำนวนหน้าจากเดิม 250 หน้าเป็นกว่า 380 หน้า ด้วยเหตุนี้จึงเปลี่ยนชื่อเรื่องใหม่เป็น ภาษาศาสตร์คลังข้อมูล : หลักการและการใช้ เพราะตั้งใจจะนำเสนอทั้งสองแนวทางคือเรื่องของหลักการและเรื่องวิธีการใช้ แนวทางแรกเป็นเรื่องหลักการความคิดต่างๆ ที่เกี่ยวข้องกับคลังข้อมูลภาษา และการใช้ประโยชน์จากคลังข้อมูลซึ่งเป็นเนื้อหาหลักของฉบับพิมพ์ครั้งแรก โดยบทที่กล่าวถึงเรื่องประโยชน์ของการใช้คลังข้อมูลภาษาเดิมที่เป็นเรื่องการเรียนการสอนภาษา การวิจัยภาษา การแปลและการประมวลผลภาษา ได้ถูกแยกออกมาเป็นแต่ละบทในฉบับนี้ให้จัดเจนขึ้น และได้เพิ่มประโยชน์ในการทำพจนานุกรม ตลอดจนกล่าวถึงคลังข้อมูลภาษา ผู้เรียนและการใช้ประโยชน์จากคลังข้อมูลภาษาผู้เรียนเพิ่มขึ้นมาอีกบทหนึ่ง'),
('A008', 1, 'ความรู้ความเข้าใจเรื่องภาษาเพื่อการแปล :จากทฤษฎีสู่การปฏิบัติ ', 'รัชนีโรจน์ กุลธำรง', '1 / 2552', '190 x 260 mm.', 'ปกอ่อน', 317, 230.00, 34.00, 9789740323501, 3, 'จุดประสงค์สำคัญประการหนึ่งของการเขียนหนังสือเล่มนี้ ก็เพื่อเผยแพร่ความรู้ทางทฤษฎีที่ใช้ได้กับการแปล และเสนอแนวทางในการฝึกปฏิบัติโดยใช้ทฤษฎีเป็นหลัก ผู้เขียนต้องการเผยแพร่การใช้ความรู้ด้านทฤษฎีเป็นแนวทางให้เกิดความรู้ความเข้าใจเรื่องภาษา เพื่อให้สามารถนำไปใช้วิเคราะห์ตัวบทอย่างมีระบบและสามารถนำไปสังเคราะห์ใช้ในการตัดสินใจเมื่อต้องแก้ปัญหาในการแปล ตลอดจนเพื่อให้มีมุมมองที่หลากหลายในการแก้ปัญหา อีกทั้งเพื่อให้สามารถอธิบายได้อย่างมีหลักเกณฑ์ว่า ทำไมจึงตัดสินใจเลือกคำแปลเช่นนั้นแทนที่จะอธิบายเพียงว่า “ฟังดูดี ฟังดูเพราะดี” อย่างที่เรามักได้ยินกันอยู่ รัชนีโรจน์ กุลธำรง'),
('A009', 1, 'แปลได้ แปลดี :ทักษะการแปลสำหรับผู้เรียนในระดับมหาวิทยาลัย', 'สุพรรณี ปิ่นมณี', '3 / 2555', '145 x 210 mm.', 'ปกอ่อน', 228, 110.00, 16.00, 9789740324409, 3, 'แปลได้ แปลดี มีเนื้อหาที่ทันสมัย เหมาะเป็นคู่มือสำหรับอาจารย์ นิสิต นักศึกษาทุกสถาบันที่มีการเรียนการสอนวิชาภาษาอังกฤษและการแปล เพราะจะช่วยให้สามารถแปลได้และแปลได้ดีด้วย (be able to translate and to translate well) ในขณะเดียวกัน “แปลได้แปลดี” (be joyful in translation) กล่าวคือ มีความสุขสนุกสนานเพลิดเพลินกับการแปลจนแปลได้ดีทั้งในเชิงคุณภาพและปริมาณ นอกจากนั้นยังมุ่งเน้นที่จะสร้างพื้นฐานทางการใช้ภาษาของผู้เรียนให้มั่นคงแข็งแรงอันจะนำไปสู่การพัฒนาทักษะการแปลทั้งจากอังกฤษเป็นไทย และจากไทยเป็นอังกฤษในระดับกลาง เพื่อต่อยอดไปสู่การแปลในระดับสูงต่อไป แปลได้ แปลดี ให้พื้นฐานในการใช้ภาษากับการแปลที่เหมาะสม สอดคล้องกับความจำเป็นและความประสงค์ของทุกท่านที่สนใจการแปล สามารถนำไปใช้งานได้จริง'),
('A010', 1, 'MI FU ON INK-STONES (HC)', 'VAN GULIK, R.H.', '1 / 2006', '175 x 250 mm.', 'ปกเข็ง', 70, 1350.00, 135.00, 9745240834, 3, NULL),

('B001', 2, 'พระคู่บารมี', 'เติมศักดิ์ ปิยะมณีพร :บรรณาธิการ', '1 / 2555', '255 x 340 mm.', 'ปกแข็ง', 300, 1800.00, 180.00, 9786163051981, 1, NULL),
('B002', 2, 'คู่มือทำบุญ ฉบับจัดหนัก', 'ศรัณยู นกแก้ว', '1 / 2555', '140 x 185 mm.', 'ปกอ่อน', 259, 269.00, 27.00, 9789742475208, 1, NULL),
('B003', 2, 'สุดยอดวัตถุมงคลหลวงปู่ศรี วัดอ่างศิลา', 'ครูดุก พัทยา (สำเรือน ทับจันทร์)', '1 / 2555', '0 x 0 mm.', 'ปกอ่อน', 80, 180.00, 27.00, 9786165143677, 1, 'รวมภาพและรายละเอียด วัตถุมงคลที่หาชมยากแท้ทุกองค์ ที่วงการให้ความสนใจ พร้อมทั้งประเมินราคาค่านิยม'),
('B004', 2, 'รวมวัตถุมงคลหลวงพ่อเริ่ม ปรโม วัดจุกกะเฌอ', NULL, '1 / 2555', '210 x 295 mm.', 'ปกอ่อน', 80, 180.00, 27.00, 9786165143776, 2, 'รวมภาพและรายละเอียดเนื้อหาสาระของพระเกจิอาจารย์แห่งฝั่งทะเลตะวันออกผู้เปี่ยมด้วยเมตตากับมงคลวัตถุที่เซียนพระหลายสำนักต่างจับตามอง พร้อมตำหนิ จุดสังเกต และประเมินค่านิยมทุกรุ่น'),
('B005', 2, 'สุดยอดวัตถุมงคลพระมฤคทายวัน', 'ครูดุก พัทยา (สำเรือน ทับจันทร์)', '1 / 2555', '0 x 0 mm.', 'ปกอ่อน', 80, 180.00, 27.00, 9786165143721, 2, 'รวมภาพและรายละเอียดเนื้อหาสาระของพระมฤคทายวัน ไว้อย่างครบถ้วนสมบูรณ์ทุดของเมืองไทย'),
('B006', 2, 'ไสยศาสตร์มนต์ดำของอาถรรพณ์ต้องห้าม', 'ว.ไสยเวท', '1 / 2555', '0 x 0 mm.', 'ปกอ่อน', 192, 140.00, 21.00, 9786165142359, 2, NULL),
('B007', 2, 'พระปิดตา-มหาอุตม์', 'ครูดุก พัทยา (สำเรือน ทับจันทร์)', '1 / 2555', '210 x 295 mm.', 'ปกอ่อน', 80, 180.00, 27.00, 9786165143639, 2, NULL),
('B008', 2, '35 เรื่องกรรม บันทึกชีวิตคนกลางไฟ', 'นที ลานโพธิ์', '1 / 2555', '0 x 0 mm.', 'ปกอ่อน', 224, 160.00, 24.00, 9786167208107, 3, NULL),
('B009', 2, 'บามิยัน ใต้รอยบาทพระพุทธเจ้า', 'สัมพันธ์ ก้องสมุทร', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 264, 250.00, 25.00, 9786167236216, 3, NULL),
('B010', 2, 'ธรรมะห่มกาย', 'เบญจธรรม รักษ์ธรรม', '1 / 2555', '130 x 185 mm.', 'ปกอ่อน', 176, 110.00, 11.00, 9789747743555, 3, NULL),

('D001', 3, 'ลิลิตนางในฝัน', 'ก้องภพ รื่นศิริ', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 194, 180.00, 27.00, 9786167514185, 1, 'ลิลิตนางในฝัน เป็นวรรณกรรมร้อยกรองยุคปัจจุบัน ประกอบด้วยลิลิตสุภาพ 3 เรื่องที่มีความเกี่ยวข้องสืบเนื่องกัน คือ ลิลิตท่าทุ่งนา ลิลิตเกาะเกร็ด และ ลิลิตวรรณศิลป์สัญจร ซึ่งทั้ง 3 เรื่องเป็น “ลิลิตเชิงนิราศ” เนื่องจากเป็นเรื่องราวที่พรรณนาถึงการพรากจาก “ดอกฟ้าดุริยศิลปิน” หรือ “นางในฝัน” อันเป็นที่รักของ “กวีวิศวกร” ผู้ดำเนินเรื่อง ลิลิตท่าทุ่งนา พรรณนาถึงการเดินทางไปศึกษาเพื่อเป็นผู้ตรวจประเมินระบบความปลอดภัยสมัยใหม่ ในฐานะวิศวกรของการไฟฟ้าฝ่ายผลิตแห่งประเทศไทย ที่เขื่อนท่าทุ่งนา จังหวัดกาญจนบุรี ในปี 2549 ประกอบด้วยร่ายสุภาพ 18 บท โคลงสองสุภาพ 36 บท โคลงสามสุภาพ 88 บท และโคลงสี่สุภาพ 96 บท รวมเป็นบทกวี 238 บท นอกจากนี้ยังมี “เพลงพวงมาลัย” ซึ่งเป็นบทกวีพื้นบ้านที่ใช้ร้องเล่นกันทางภาคกลางอีก 5 บท โดยสัมผัสเชื่อมต่อระหว่างร่ายกับโคลง และสามารถตัดออกได้โดยที่ร่ายกับโคลงยังสัมผัสกันหรือ “เข้าลิลิต” อย่างถูกต้อง ลิลิตเกาะเกร็ด พรรณถึงการเดินทางไปสัมมนาด้านการบริหาร ที่เกาะเกร็ด จังหวัดนนทบุรี ในปี 2551 เป็นเหตุการณ์หลังลิลิตท่าทุ่งนา 2 ปี ประกอบด้วย ร่ายสุภาพ 24 บท โคลงสองสุภาพ 48 บท โคลงสามสุภาพ 62 บท และโคลงสี่สุภาพ 170 บท รวมเป็นบทกวี 304 บท ลิลิตวรรณศิลป์สัญจร พรรณนาถึงการทัศนศึกษาร่วมกับเพื่อนรุ่นน้องที่ยังเป็นนิสิตสมาชิกชมรมวรรณศิลป์ มหาวิทยาลัยเกษตรศาสตร์ในปี 2525 จึงเป็นลิลิตเรื่องแรกที่ประพันธ์ขึ้นในวัยหนุ่ม เป็นเหตุการณ์ก่อน ลิลิตท่าทุ่งนา 24 ปี และก่อนลิลิตเกาะเกร็ด 26 ปี ประกอบด้วยร่ายสุภาพ 7 บท โคลงสองสุภาพ 10 บท โคลงสามสุภาพ 15 บท และโคลงสี่สุภาพ 55 บท รวมเป็นบทกวี 87 บท ลิลิตนางในฝัน ซึ่งประกอบด้วยลิลิตทั้ง 3 เรื่อง จึงเป็นบทกวีที่ยาวถึง 631 บท (รวมโคลงสี่สุภาพ บทส่งท้ายด้วย 2 บท) และมี “เพลงพวงมาลัย” อีก 5 บท นอกจากนี้ยังสอดแทรก “กลอักษร” และ “กลบท” ทั้งที่มีรูปแบบอยู่เดิมและที่สร้างสรรค์ใหม่รวมหลายรูปแบบ ซึ่งได้อธิบายไว้ในภาคผนวก'),
('D002', 3, 'คำหยาด (กวีนิพนธ์เล่มแรกของกวีรัตนโกสินทร์และศิลปินแห่งชาติ)', 'เนาวรัตน์ พงษ์ไพบูลย์', '10 / 2554', '130 x 185 mm.', 'ปกอ่อน', 191, 175.00, 17.00, 9789744754417, 1, NULL),
('D003', 3, 'แม่ ธรรมแท้ของชีวิต', 'ภาณุมาศ ภูมิถาวร', '1 / 2554', '190 x 190 mm.', 'ปกอ่อน', 78, 115.00, 11.00, 9786167562032, 2, NULL),
('D004', 3, 'ไขคำแก้วคำแพง พินิจวรรณกรรมไทย-ไท', 'ประคอง นิมมานเหมินท์', '1 / 2554', '145 x 210 mm.', 'ปกอ่อน', 415, 300.00, 45.00, 9789740328056, 2, 'คำ ในภาษาไทและภาษาไทยโบราณหมายถึง ถ้อยคำ , ความ หรือเรื่องราว ส่วน แก้ว หมายถึง อัญมณี หรือสิ่งที่มีค่า คำแก้วคำแพง ในที่นี้ หมายถึง วรรณกรรมไทยและวรรณกรรมของชนชาติไทซึ่งนับเป็นสมบัติล้ำค่าอย่างหนึ่งในด้านวัฒนธรรม หนังสือเล่มนี้เป็นการรวมบทความที่ผู้เขียนได้ศึกษาวิเคราะห์วรรณกรรมไทย – ไทหลายประเภท ได้แก่ นิทาน ตำนาน และมหากาพย์ รวบรวมจากชนชาติไทหลายกลุ่ม จากข้อมูลที่ถ่ายทอดทางมุขปาฐะและลายลักษณ์ มีทั้งร้อยแก้วและร้อยกรอง'),
('D005', 3, 'เรียนรู้ 100 บทกวีญี่ปุ่นโบราณ', 'อรรถยา สุวรรณระดา', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 292, 330.00, 50.00, 9789740330257, 2, 'เฮียะกุนินอิฌฌุ เป็นหนังสือรวมบทกลอนญี่ปุ่นซึ่งรวบรวมขึ้นราว ค.ศ.1235 ซึ่งตรงกับสมัยคะมะกุระ ของญี่ปุ่น ฟุจิวะระ โนะ ซะดะอิเอะ ได้คัดเลือกบทกลอนของกวีที่มีชื่อเสียงจำนวน 100 คน โดยเลือกมาคนละบท หนังสือรวมกลอนนี้ได้รับการยกย่องให้เป็นคู่มือขั้นต้นในการแต่งกลอนวะกะหรือกลอนญี่ปุ่น และยังเป็นหนังสือรวมกลอนที่ผู้เริ่มศึกษากลอนญี่ปุ่นนิยมหยิบยกมาศึกษา นอกจากนี้กลอนในเฮียะกุนินอิฌฌุยังเป็นที่รู้จักกันแพร่หลายเนื่องจากกลอนทั้ง 100 บทนี้ได้ปรากฏอยู่บนไพ่คะรุตะ ของญี่ปุ่นซึ่งนิยมเล่นกันในวันปีใหม่ด้วย ในหนังสือเล่มนี้ ผู้เขียนได้หยิบยกกลอนเฮียะกุนินอิฌฌุทั้ง 100 บทมาถอดความเป็นภาษาไทยแบบง่ายๆ พร้อมทั้งอธิบายเทคนิคการประพันธ์กลอนญี่ปุ่นซึ่งปรากฎอยู่ในบทกลอนแต่ละบท รวมทั้งข้อมูลโดยสังเขปเกี่ยวกับกวีผู้แต่งกลอนเหล่านั้นซึ่งล้วนแล้วแต่เป็นกวีเอกของญี่ปุ่นในช่วงตั้งแต่สมัยนารา จนถึงสมัยคะมะกุระ (ช่วงศตวรรษที่ 7 ถึง 13) โดยหวังว่าหนังสือเล่มนี้จะช่วยก่อให้เกิดความก้าวหน้าทางวิชาการและช่วยเสริมสร้างความรู้และเป็นประโยชน์ต่อสาขาวิชาภาษาญี่ปุ่นโดยเฉพาะในด้านการศึกษากลอนญี่ปุ่น'),
('D006', 3, 'รื่นรสรักในวรรณคดี', 'เอกรงค์ ภาณุพงษ์', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 140, 120.00, 24.00, 9786165084451, 3, NULL),
('D007', 3, 'สุนทรภู่บอกเหตุน้ำท่วมจากพุทธทำนาย', 'ทศพร วงศ์รัตน์', '1 / 2554', '145 x 210 mm.', 'ปกอ่อน', 138, 120.00, 18.00, 9789747622751, 3, 'ผู้เขียนมีความโน้มเอียงเชื่อว่า เรื่องน้ำท่วมจักรวาลที่มีกล่าวอยู่ในหนังสือ “ไตรภูมิกถา” หรือ ไตรภูมิพระร่วง น่าจะมีส่วนไม่น้อยในการเสริมสร้างความนึกคิดให้สุนทรภู่ใช้แต่ง “กาพย์พระไชยสุริยา” และข้อมูลหนึ่งที่มีเค้าเชิงผสมผสาน จนสุนทรภู่นำมาประกอบในการแต่ง “กาพย์พระไชยสุริยา” คือ จากประวัติความเป็นมาของพระพุทธรูปปางมารวิชัย ที่มีอยู่ว่า ในครั้งพญามารวสวตี (พญาวัสวดีมาร) ยกทัพมาทำลายความเพียรของพระบรมโพธิสัตว์ ขณะประทับ ณ โพธิบัลลังก์ ทัพพญามารที่มีมากมายล้วนใหญ่โต พร้อมศัตรารุกเข้าทำลายจนเหล่าเทวดาหนีไปหมด แต่พระบรมโพธิสัตว์มิได้หวาดกลัว บรรดาอาวุธที่ซัดเข้าใส่พระองค์ กลับกลายเป็นมาลัยดอกไม้ไปหมด แต่พญามารยังทวงว่า รัตนบัลลังก์นี้เป็นของตน จึงทรงกล่าวตอบว่า บัลลังก์นี้เกิดมีมาด้วยบุญกุศลที่พระองค์สั่งสมมาแต่ปางก่อน โดยมีแม่พระธรณีเป็นพยาน เมื่อได้ถูกพาดพิง แม่พระธรณีจึงแสดงโดยการปล่อยมวยผม แล้วบีบน้ำกรวดอุทิศผลบุญที่รวบรวมไว้ทุกครั้งจากการทำทานสั่งสมตั้งแต่ครั้งอดีตอันยาวนานของพระบรมโพธิสัตว์ จนน้ำได้ไหลพัดพาทำลายเหล่ามารจนหมดสิ้น จึงมีคำถามว่าทำไมตำนานน้ำท่วมโลกถึงมีการเล่าขานกันอย่างกว้างขวาง เรียกได้ว่าทุกอารยธรรมสำคัญของโลกตลอดมาจนถึงปัจจุบัน คำตอบเรื่องนี้ก็คงเพราะความเลวร้ายมีเกิดขึ้นโดยคน คล้ายเป็นวัฏจักรหนึ่งของมนุษย์โลก เช่นเดียวกับโรคระบาด ไฟบรรลัยกัลป์ และน้ำท่วมใหญ่ ที่หลายครั้งได้สร้างความวิบัติอย่างมหันต์ และการล้มตายแก่มวลมนุษยชาติ จนถึงขั้นสิ้นชาติ สิ้นวัฒนธรรม ด้วยความเกรงกลัวของคนรุ่นต่อมา จากประวัติดังกล่าว และกฎแห่งการรักษาความสมดุลของธรรมชาติ ที่อะไรก็ตามถ้ามากเกินไปจะทำลายตัวมันเอง แต่เมื่อมีผลกระทบที่รุนแรงต่อคน และคนครั้งบรรพบุรุษอธิบายไม่ได้ คนก็ใช้วิธีอธิบายว่า มาจากอำนาจเหนือธรรมชาติ สำหรับเรื่องน้ำท่วมโลก เมื่อน้ำมีคุณสมบัติการทำลาย และล้างที่ดีที่สุด อีกทั้งมีจำนวน และพลังมหาศาล คนเราจึงพยายามที่จะพาดพิงถึงน้ำมาใช้อธิบายเหตุการณ์นี้ ว่าเป็นการล้างโลก เพื่อเริ่มโลกใหม่ที่สะอาด และน่าอยู่กว่า'),
('D008', 3, 'สัจนิยมมหัศจรรย์ในวรรณกรรมของ กาเบรียล การ์เซีย มาร์เกซ', 'สุรเดช โชติอุดมพันธ์', '1 / 2554', '145 x 210 mm.', 'ปกอ่อน', 291, 230.00, 34.00, 9789740327899, 3, 'กาเบรียล การ์เซีย มาร์เกซ เป็นนักเขียนที่มีชื่อเสียงมากที่สุดคนหนึ่งในทวีปลาตินอเมริกา ที่พัฒนารูปแบบสัจนิยมมหัศจรรย์จนเป็นที่รู้จักและส่งอิทธิพลต่อนักเขียนวรรณกรรมทั่วโลก หนังสือเรื่อง สัจนิยมมหัศจรรย์ในวรรณกรรมของ กาเบรียล การ์เซีย มาร์เกซ เป็นการศึกษาวิจัยงานเขียนทั้งนวนิยายและเรื่องสั้นแนวสัจนิยมมหัศจรรย์ของการ์เซีย มาร์เกช ด้วยเล็งเห็นว่าเขาไม่ได้ใช้รูปแบบสัจนิยมมหัศจรรย์เพียงเพื่อแสดงให้เห็นความมหัศจรรย์และทำให้ผู้อ่านตื่นตาตื่นใจไปกับการผสมผสานระหว่างองค์ประกอบที่มหัศจรรย์เหนือจริงกับฉากอันธรรมดาสามัญเท่านั้น หากแต่ยังนำเสนอให้เห็นมิติการวิพากษ์วิจารณ์สังคมและวัฒนธรรมที่ซ่อนอยู่ในรูปแบบดังกล่าว'),
('D009', 3, 'ประวัติวรรณคดีญี่ปุ่น', 'อรรถยา สุวรรณระดา', '1 / 2554', '145 x 210 mm.', 'ปกอ่อน', 204, 120.00, 18.00, 9789740327905, 3, 'หนังสือเล่มนี้มีเนื้อหาครอบคลุมประวัติวรรณคดีญี่ปุ่นตั้งแต่สมัยโบราณก่อนที่ญี่ปุ่นจะมีตัวอักษรใช้จนกระทั่งถึงสมัยเอโดะซึ่งเป็นยุคก่อนที่วัฒนธรรมตะวันตกจะเข้ามามีอิทธิพลต่อแวดวงวรรณกรรมญี่ปุ่นผู้เขียนได้ประมวลเนื้อหาที่สำคัญของวรรณคดีญี่ปุ่นโบราณและได้นำตัวอย่างจากวรรณกรรมซึ่งคัดมาจากตอนที่สนุกหรือเป็นที่รู้จักกันดีในหมู่คนญี่ปุ่นเพื่อให้ผู้อ่านได้มีโอกาสอ่านต้นฉบับวรรณกรรมโบราณและเข้าใจลักษณะผลงานวรรณกรรมชิ้นนั้นๆ และได้สรุปเรื่องย่อของวรรณกรรมที่สำคัญบางชิ้นเอาไว้ด้วย เป็นการปูความรู้พื้นฐานเกี่ยวกับวิวัฒนาการและลักษณะวรรณกรรมญี่ปุ่นในแต่ละยุคสมัยข้างต้น เหมาะสำหรับผู้เริ่มต้นศึกษาวรรณคดีญี่ปุ่นและผู้สนใจวรรณคดีญี่ปุ่นทั่วไป'),
('D010', 3, 'ก่อร่าง สร้างเรื่อง :เรื่องเล่า อัตลักษณ์และชุมชน ในวรรณกรรมสตรีชายขอบ', 'ชุติมา ประกาศวุฒิสาร', '1 / 2554', '145 x 210 mm.', 'ปกอ่อน', 255, 210.00, 32.00, 9786167150062, 3, ' ใน My Place มอร์แกนกล่าวว่าข้อมูลของกลุ่มคนอะบอริจินถูกเก็บไว้ในแฟ้มรัฐบาลผิวขาวออสเตรีเลียมีนโยบายปกปิดข้อมูลเกี่ยวกับคนพื้นเมือง เนื่องจากข้อมูลเหล่านี้บ่งบอกความโหดร้ายทารุณของรัฐที่กระทำต่อคนเหล่านี้ - ใน Like Water for Chocolate หลังจากที่ตัวละครเคร์ตรูดิสหนีออกจากบ้านไป เอเลนาสั่งห้ามไม่ให้สมาชิกในบ้านเอ่ยถึงเธออีกต่อไป ทั้งยังได้เผาข้างของเครื่องใช้ของเธออีกด้วยการลงโทษที่เธอฝ่าฝืนจารีตของสังคมเม็กซิกัน - ตัวละครอกิโกะใน Comfort Woman รู้สึกราวกับว่าเธอตายจากโลกนี้ไปแล้ว นับตั้งแต่พี่สาวของเธอได้ขายเธอให้แก่กองทัพญี่ปุ่นเพื่อเป็นหญิงบำเรอในค่ายทหาร อกิโกะเป็นเหยื่อของอุดมการณ์ชายเป็นใหญ่ในเกาหลีที่ให้ความสำคัญแก่ลูกชายมากกว่าลูกสาว - ใน Obasan โคกาวะเปิดเรื่องด้วยถ้อยคำรำพึงเกี่ยวกับความเงียบ และความว่างเปล่าที่เกิดขึ้นกลังจากคำสั่งของรัฐบาลผิวขาวแคนาดาที่ให้กลุ่มคนแคนาดาเชื้อสายญี่ปุ่นอพยพจากชุมชนของตนในช่วงสงครามโลกครั้งที่สอง นอกจากนี้รัฐบาลยังทำลายหลักฐานการตั้งถิ่นฐานของชุมชนแคนาดาเชื้อสายญี่ปุ่น - ใน Red Azalea ความเงียบเป็นผลมาจากนโยบายของพรรคคอมมิวนิสต์ภายใต้การนำของเหมาที่ให้ความสำคัญแก่เรื่องส่วนตัว การเมืองเข้ามาแทรกแซงเรื่องส่วนตัวในทุกด้าน จนกระทั่งสังคมในยุคนั้นไม่เหลือพื้นที่ทางสังคมให้กับการแสดงออกใดๆ ก็ตามที่เกี่ยวข้องกับเรื่องส่วนตัว'),

('F001', 4, 'ANSWER BOOK: ตอบทุกคำถามที่คุณอยากรู้', 'NATIONAL GEOGRAPHIC และคณะ', '1 / 2555', '230 x 285 mm.', 'ปกแข็ง', 176, 711.00, 79.00, 9789742477653, 1, NULL),
('F002', 4, '101 เรื่อง ต้นกำเนิดของใช้รอบตัว (101 STORIES BEHIND THE ORIGIN OF EVERYDAY THINGS)', 'SEEMA GUPTA', '1 / 2555', '145 x 210 mm.', 'ปกอ่อน', 195, 112.00, 13.00, 9789745472341, 1, 'คุณเคยรู้หรือไม่ ว่าของใช้รอบๆ ตัวในชีวิตประจำวันนั้น มีต้นกำเนิดจากไหน อย่างไร!!! หนังสือ “101 เรื่องต้นกำเนิดของใช้รอบตัว” เล่มนี้ ได้ขุดค้นเรื่องราวการประดิษฐ์ ค้นพบ หรือสร้างสิ่งของที่ผ่านการคัดเลือกมาแล้ว 101 อย่าง ซึ่งเป็นของที่เราใช้อยู่ทุกวัน หนังสือเล่มนี้ ต่างจากหนังสือประเภทเดียวกัน เล่มอื่นๆ ตรงที่ เราไม่เพียงแต่สืบค้นความเป็นมาของสิ่งของเหล่านี้เท่านั้น แต่ยังเล่าเกร็ดน่าสนใจ เล็กๆ น้อยๆ บางอย่างที่เกี่ยวข้องด้วย ซึ่งรับรองว่าไม่มีให้หาได้อ่านง่ายๆ ในหนังสือเล่มอื่น มาค้นหาคำตอบกันว่า ต้นกำเนิดของของใช้รอบตัวมาจากไหน และมีเส้นทางการเดินทางอย่างไร กว่าจะพัฒนามาเป็นของที่คุณใช้อยู่ในทุกวันนี้ ไม่แน่...หนังสือเล่มนี้ อาจเป็นอีกหนึ่งแรงบันดาลใจให้คุณค้นพบอะไรใหม่ๆ ให้กับผู้คนก็ได้ หาคำตอบได้แค่ปลายนิ้วพลิก...'),
('F003', 4, 'มหัศจรรย์แห่ง "พืช" :เสริมสร้างความรู้ทางวิทยาศาสตร์เกี่ยวกับธรรมชาติของพืช', 'อลิษา โอฬารรัตน์มณี :บรรณาธิการ', '1 / 2555', '215 x 240 mm.', 'ปกอ่อน', 68, 68.00, 7.00, 9786167738147, 1, 'หนังสือเรื่อง มหัศจรรย์แห่ง “พืช” เป็นหนังสือการ์ตูนเสริมสร้างความรู้ทางวิทยาศาสตร์ เกี่ยวกับธรรมชาติของพืช ช่วยให้เด็กๆ ได้รู้จักกับ ส่วนประกอบ การเจริญเติบโต การสร้างอาหาร และประโยชน์ของพืช ด้วยภาพสี่สีสวยงาม เพื่อดึงดูดความสนใจให้กับเด็กๆ ช่วยพัฒนาสมาธิและสติปัญญาให้ลูกน้อย ซึ่งจะทำให้เด็กมีนิสัยที่รักการอ่าน และช่างสังเกตจดจำเมื่อเติบโตขึ้นเป็นผู้ใหญ่ต่อไป'),
('F004', 4, 'หนังสือชุดเพื่อการเรียนรู้', 'วัฏฏะ คลาสสิฟายด์ส', '1 / 2555', '290 x 210 mm.', 'ปกอ่อน', 607, 845.00, 149.00, 8857121316532, 1, NULL),
